import Vue from 'vue'
import VueRouter from 'vue-router'
import Main from '../views/MainView.vue'
import Message from '../views/MessageView.vue'
import Credit from "../views/CreditView.vue"

Vue.use(VueRouter)

const routes = [
  {
    // App.vue에서 지정한 경로를 여기서 연결해준다.
    // APP.vue에서 지정한 경로와 일치
    path: '/',
    name: 'Main',
    // App.vue의 router-view에 home 컴퍼넌트로 되어있다.
    component: Main
  },
  {
    // APP.vue에서 지정한 경로와 일치
    path: '/message',
    name: 'Message',
    // 상단에 import를 해야 가져오기 가능
    component: Message
  },
  {
    path: '/credit',
    name: 'Credit',
    component: Credit
  }
]

const router = new VueRouter({
  // 뒤로가기 지원
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router
